using System; using System.Collections.Generic; using System.Text;  namespace DIAS.Events {     /// <summary>     /// Interface defines the bus for handling domain event.     /// </summary>     public interface IEventBus     {         void Publish(DomainEvent @event);          void Subscribe<T, TH>()             where T : DomainEvent             where TH : IDomainEventHandler<T>;          void Unsubscribe<T, TH>()            where TH : IDomainEventHandler<T>            where T : DomainEvent;          void SubscribeDynamic<TH>(string eventName)             where TH : IDynamicDomainEventHandler;          void UnsubscribeDynamic<TH>(string eventName)             where TH : IDynamicDomainEventHandler;           } } 